create table `bmk_users` (`users_id` bigint unsigned not null auto_increment primary key, `users_username` varchar(32) not null, `users_password` varchar(64) not null, `users_is_admin_approved` char(1) not null default 'n', `users_created_at` datetime not null, `users_updated_at` datetime not null) default character set utf8mb4 collate 'utf8mb4_unicode_ci';
create table `bmk_posts` (`posts_id` bigint unsigned not null auto_increment primary key, `posts_title` varchar(256) not null, `posts_url` varchar(256) not null, `posts_description` varchar(2048) not null, `posts_user_id` bigint unsigned null, `posts_created_at` datetime not null, `posts_updated_at` datetime not null) default character set utf8mb4 collate 'utf8mb4_unicode_ci';
alter table `bmk_posts` add constraint `bmk_posts_posts_user_id_foreign` foreign key (`posts_user_id`) references `bmk_users` (`users_id`) on delete cascade;
create table `bmk_contacts` (`contacts_id` bigint unsigned not null auto_increment primary key, `contacts_name` varchar(32) not null, `contacts_email` varchar(64) not null, `contacts_phone` varchar(16) not null, `contacts_message` varchar(2048) not null, `contacts_created_at` datetime not null, `contacts_updated_at` datetime not null) default character set utf8mb4 collate 'utf8mb4_unicode_ci';
create table `bmk_admin` (`admin_id` bigint unsigned not null auto_increment primary key, `admin_username` varchar(32) not null, `admin_password` varchar(64) not null, `admin_created_at` datetime not null, `admin_updated_at` datetime not null) default character set utf8mb4 collate 'utf8mb4_unicode_ci';