create table `ilp_admin` (`admin_id` int unsigned not null auto_increment primary key, `admin_username` varchar(32) not null, `admin_password` varchar(64) not null, `admin_created_at` datetime not null, `admin_updated_at` datetime not null);
create table `ilp_websites` (`websites_id` bigint unsigned not null auto_increment primary key, `websites_domain` varchar(32) not null, `websites_title` varchar(32) not null, `websites_description` varchar(2048) not null, `websites_content` longtext not null, `websites_created_at` datetime not null, `websites_updated_at` datetime not null);
alter table `ilp_websites` add unique `ilp_websites_websites_domain_unique`(`websites_domain`);
ALTER TABLE `ilp_websites` ADD `websites_is_default` char(1) DEFAULT 'n' NOT NULL AFTER `websites_content`, ADD `websites_logo` varchar(24) NOT NULL AFTER `websites_content`, ADD `websites_header_info` LONGTEXT NOT NULL AFTER `websites_content`, ADD `websites_footer_info` LONGTEXT NOT NULL AFTER `websites_content`;
create table `ilp_banners` (`banners_id` bigint unsigned not null auto_increment primary key, `banners_title` varchar(32) not null, `banners_description_1` varchar(2048) not null, `banners_description_2` varchar(2048) not null, `banners_image` varchar(256) not null, `banners_url` varchar(256) not null, `banners_border_color` varchar(10) not null, `banners_border_color_hover` varchar(10) not null, `banners_website_id` bigint unsigned null, `banners_created_at` datetime not null, `banners_updated_at` datetime not null);
alter table `ilp_banners` add constraint `ilp_banners_banners_website_id_foreign` foreign key (`banners_website_id`) references `ilp_websites` (`websites_id`) on delete cascade